
CREATE TABLE ALLOW_SHARING_GROUP
(
	ALLOW_SHARING_GROUP_CD  VARCHAR2(15 CHAR)  NOT NULL ,
	ALLOW_SHARING_GROUP_TXT  VARCHAR2(240 CHAR)  NOT NULL ,
	ALLOW_SHARING_GROUP_FR_TXT  VARCHAR2(240 CHAR)  NULL ,
	CREATE_TS             TIMESTAMP(6)  NOT NULL ,
	CREATE_USER_ID        VARCHAR2(40 CHAR)  NOT NULL ,
	LAST_UPDT_TS          TIMESTAMP(6)  NOT NULL ,
	LAST_UPDT_USER_ID     VARCHAR2(40 CHAR)  NOT NULL 
