CREATE UNIQUE INDEX PK_Allowance_Sharing_Group ON ALLOW_SHARING_GROUP
(ALLOW_SHARING_GROUP_CD  ASC);


ALTER TABLE ALLOW_SHARING_GROUP
	ADD CONSTRAINT  PK_Allowance_Sharing_Group PRIMARY KEY (ALLOW_SHARING_GROUP_CD);

